package com.java5200.date0526;

//应避免String类的这种使用
public class StringBufferDemo09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str1 = "Zhangwuji";
		for (int i = 0; i < 100; i++) {
			str1 += i; // 不断修改String的内存引用,性能低
		}
		System.out.println(str1);
	}

}
